def create_collect(attrs, prayer_book_id)
  attrs[:prayer_book_id] = prayer_book_id

  # Check for existing to avoid duplicates if re-run
  existing = Collect.find_by(
    prayer_book_id: prayer_book_id,
    celebration_id: attrs[:celebration_id],
    sunday_reference: attrs[:sunday_reference],
    season_id: attrs[:season_id],
    text: attrs[:text]
  )

  return if existing

  Collect.create!(attrs)
end

